import React from "react"
import { render } from "@testing-library/react"


class Word extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            word: ""
        }
    }
    setGuesseWord(e){
        if (e.key ==="Enter")
            console.log(this.state.word)
        else
            this.state.word += e.keys
    }
    render(){
        return (    
            <form> 
                <input 
                    placeholder="Word to guesses" 
                    onKeyDown={(e) => this.setGuesseWord(e)}
                    autoFocus
                ></input>
            </form>
        )}
}

export default Word
