import React from "react"
import Word from "./word"
import ShowGuesse from "./Guesse"
import Letter from "./Letter"
import Choice from "./Choice"

class Game extends React.Component
{
    constructor(props){
        super(props)
        this.onSubmitWord = this.onSubmitWord.bind(this)
        this.onWordChange = this.onWordChange.bind(this)
        this.state = {
            tmpWord: "",
            word: "",
            guesses: []
        }
    }
    onSubmitWord(event){
        event.preventDefault();
        let word = this.state.tmpWord.toString()
        let upperWord = word.toUpperCase()
        this.setState({
            word: upperWord,
            guesses: this.formateString(upperWord)
        })
        event.target.querySelector("input").value = ""
    }

    onWordChange(e){
        this.setState({
            tmpWord: e.target.value
        })
    }

    formateString(word){
        let words = word.split("");
        let cachedword = [];
        for(let word of words)
            cachedword.push(word === " " ? "\xa0" : "_ ")
        return cachedword;
    }
    render(){
     return (
         <div>
            <Word 
               onSubmitWord = {this.onSubmitWord}
               onWordChange = {this.onWordChange}
                value = {this.word}
            />
            <p>{this.state.word}</p>
            <ShowGuesse word={this.state.guesses}/>
            <Choice />
        </div>
     )
    }
}


export default Game
