import React from "react"
import Word from "./word"

class Game extends React.Component
{
    constructor(props){
        super(props)
        this.onSubmitWord = this.onSubmitWord.bind(this)
        this.onWordChange = this.onWordChange.bind(this)
        this.state = {
            tmpWord: "",
            word: ""
        }
    }
    onSubmitWord(word){
        word.preventDefault();
        console.log(word.target.querySelector("input"))
        this.setState({
            word: this.state.tmpWord.toString.toUpperCase()
        })
        word.target.querySelector("input").value = ""
    }

    onWordChange(e){
        this.setState({
            tmpWord: e.target.value
        })
    }
    render(){
     return (
         <div>
            <Word 
               onSubmitWord = {this.onSubmitWord}
               onWordChange = {this.onWordChange}
                value = {this.word}
            />
            <p>{this.state.word}</p>
        </div>
     )
    }
}


export default Game
