import React from "react"
import Word from "./word"

class Game extends React.Component
{
    constructor(props){
        super(props)
        this.state = {
            tmpWord: "",
            word: ""
        }
    }
    onSubmitWord(word){
        word.preventDefault();
        console.log(this.state.word)
    }
    onWordChange(e){
        this.setState({
            tmpWord: e.target.value
        })
    }
    render(){
     return (
            <Word 
               onSubmitWord = {this.onSubmitWord}
               onWordChange = {this.onWordChange}
                value = {this.word}
            />
     )
    }
}


export default Game
