import React from "react"
import Word from "./word"
import Guesse from "./Guesse"

class Game extends React.Component
{
    constructor(props){
        super(props)
        this.onSubmitWord = this.onSubmitWord.bind(this)
        this.onWordChange = this.onWordChange.bind(this)
        this.state = {
            tmpWord: "",
            word: ""
        }
    }
    onSubmitWord(event){
        event.preventDefault();
        let word = this.state.tmpWord.toString()
        let upperWord = word.toUpperCase()
        this.setState({
            word: upperWord
        })
        event.target.querySelector("input").value = ""
    }
    onWordChange(e){
        this.setState({
            tmpWord: e.target.value
        })
    }
    render(){
     return (
         <div>
            <Word 
               onSubmitWord = {this.onSubmitWord}
               onWordChange = {this.onWordChange}
                value = {this.word}
            />
            <p>{this.state.word}</p>
            <Guesse word={this.state.word}/>
        </div>
     )
    }
}


export default Game
