import React from "react"


class Word extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            word: ""
        }
    }

    render(){
        return (
            <div>
                <form onSubmit={this.props.onSubmitWord}> 
                    <input 
                        placeholder="Word to guesses" 
                        autoFocus
                    ></input>
                </form>
                <button onSubmit={this.props.onSubmitWord}>save</button>
            </div>    
        )}
}

export default Word
